import "./App.css";
import { useEffect, useState } from "react";
import Shelves from "./Components/Shelves";
import * as BooksAPI from "./BooksAPI";
import SingleBook from "./Components/SingleBook";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [search, setSearch] = useState("");
  const [searchBooks,setSearchBooks] = useState([]);

  useEffect(()=>{
    BooksAPI.getAll().then(data=> {
      setBooks(data);
      console.log(data);
    })
  },[]);

  useEffect(()=>{
    let searchIsActive = true;
    if(search){
      BooksAPI.search(search).then(data=>{
        if(data.error){
          console.log('Nothing Found');
          setSearchBooks([]);
        }else{
          if(searchIsActive){
            console.log(data);
            setSearchBooks(data);
          }
        }
      });
    }
    return() => {
      searchIsActive = false;
    }
  },[search]);

  const [books,setBooks] = useState([
    // {
    //   id:1,
    //   bookCover: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api",
    //   bookTitle: "To Kill a Mockingbird",
    //   bookAuthors: ["Harper Lee"],
    //   readingStatus: "currentlyReading"
    // },
    // {
    //   id:2,
    //   bookCover: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api",
    //   bookTitle: "Test Book Name",
    //   bookAuthors: ["Test Lee"],
    //   readingStatus: "read"
    // }
  ]);


const changeReadingStatus = (singleBook, newReadingStatus)=>{
   const updateBooks = books.map((b)=>{
    if(b.id === singleBook.id){
      singleBook.shelf = newReadingStatus;
      return singleBook;
    }
    return b;
   })
   setBooks(updateBooks);
   BooksAPI.update(singleBook, newReadingStatus).then(data=>data);
}

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
                value={search}
                onChange={(event)=>setSearch(event.target.value)}/>
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid">
            {searchBooks.map((searchBook)=> 
                    <li key={searchBook.id}>
                        <SingleBook singleBook={searchBook} changeReadingStatus={changeReadingStatus}/>
                    </li>
                    )}
            </ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <Shelves books={books} changeReadingStatus={changeReadingStatus}/>
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
