const SingleBook = ({singleBook, setBooks})=>{
    const changeReadingStatus = (singleBook, newReadingStatus)=>{
        setBooks(singleBook.readingStatus.newReadingStatus);
    }
    return(
        <div className="book">
            <div className="book-top">
            <div
                className="book-cover"
                style={{
                width: 128,
                height: 193,
                backgroundImage:
                    `url("${singleBook.bookCover}")`,
                }}
            ></div>
            <div className="book-shelf-changer">
                <select defaultValue={singleBook.readingStatus} onChange={(event)=>changeReadingStatus(singleBook,event.target.value)}>
                <option value="none" disabled>
                    Move to...
                </option>
                <option value="currentlyReading">Currenty Reading</option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
                </select>
            </div>
            </div>
            <div className="book-title">{singleBook.bookTitle}</div>
            <div className="book-authors">{singleBook.bookAuthors}</div>
      </div>
    );
}

export default SingleBook;
