import BookShelf from "./BookShelf";
const Shelves = ({books})=>{
    const currentlyReadingBooks = books.filter((book)=>book.readingStatus == "currentlyReading");
    const wantToReadBooks = books.filter((book)=>book.readingStatus == "wantToRead");
    const readBooks = books.filter((book)=>book.readingStatus == "read");
    const bookShevesHeading = ["Currently Reading", "Want To Read", "Read"];

    return(
        <div>
            <div className="bookshelf">
            <h2 className="bookshelf-title">Currently Reading</h2>
            <BookShelf books={currentlyReadingBooks}/>
            <BookShelf books={wantToReadBooks}/>
            <BookShelf books={readBooks}/>
            </div>
        </div>
    );
}

export default Shelves;
